import React, { useState } from 'react';
import { generateUniqueID } from 'web-vitals/dist/modules/lib/generateUniqueID';

import TodoList from './TodoList';

const INITIAL_DATA = [
  {
    itemName: 'default item',
    itemStatus: false,
    id: generateUniqueID()
  },
  {
    itemName: 'another item',
    itemStatus: false,
    id: generateUniqueID()
  },
  {
    itemName: 'and another item',
    itemStatus: true,
    id: generateUniqueID()
  }
];

const App = () => {
  // TO ASK should this be in app or todolist
  // here, todoitems update -> whole list rerender
  // in todolist, only updated items rerender?
  const [todoItems, setTodoItems] = useState(INITIAL_DATA);

  const handleItemChange = (id) => {
    // eslint-disable-next-line no-debugger
    debugger;
    const newTodoItems = todoItems.map((item) => {
      return item.id === id ? { ...item, itemStatus: true } : item;
    });
    console.log(newTodoItems);
    setTodoItems(newTodoItems);
  };

  return (
    <>
      <TodoList data={todoItems} onItemChange={handleItemChange} />
    </>
  );
};

export default App;
